import { NEW_USER, USER_LOGIN, USER_LOGOUT, PAGE_RELOAD } from '../actions/types'
import { API } from '../containers/MainPage'

    export const pageReload = () => dispatch => {
        const { token } = localStorage.getItem('user-token')
        if( token !== "undefined" && token !== null) {
            fetch(`${API}users/current_user`, {
                headers: {
                'Authorization': `Bearer ${token}`,
                // 'Content-Type': 'application/json',
                // Accept: 'application/json'
                }
            })
            .then(res => res.json())
            .then(res => dispatch({
                type: PAGE_RELOAD,
                payload: {
                    currentUser: res, 
                    isLoggedIn: true, 
                    token: res.token}
                })
            )
        }
    }

    export const userLogin = (e) => async dispatch => {
        const { username, password } = this.state;
		e.preventDefault();
		await fetch(API + 'login', {
			method: 'POST',
			headers: { 'Content-Type': 'application/json', Accept: 'application/json' },
			body: JSON.stringify({ user: { username: username, password: password } })
		})
			.then(res => res.json())
			.then(res => {
				res.errors ? 
				alert(res.errors)
				:
				this.setState({
					currentUser: res,
					isLoggedIn: true,
					token: res.token,
					password: '',
					passwordConfirmation: ''
				})
			}
			)
			if(this.state.token !== null){
				localStorage.setItem('user-token', this.state.token);
			}
    }

    export const userLogout = () => dispatch => {
        localStorage.clear()
        dispatch({
            type: USER_LOGOUT,
            payload: {isLoggedIn: false}
        })
    }

    export const newUser = ( username, password, passwordConfirmation) => dispatch => {
		fetch(API + 'users', {
			method: 'POST',
			headers: { 'Content-Type': 'application/json', Accept: 'application/json' },
			body: JSON.stringify({
				user: {
					username: username,
					password: password,
					password_confirmation: passwordConfirmation
				}
			})
		})
			.then(res => res.json())
			.then(res => {
				res.error
					? alert(res.error)
					: dispatch({
                        type: NEW_USER,
                        payload: {
							currentUser: res,
							isLoggedIn: true,
							token: res.token,
                        }
					  });
			})
    }
